INSERT INTO `singer` VALUES (5, 'PO8', 1, '/img/singerPic/1620305663881po8.jpg', '1996-09-30 00:00:00', '中国', 'PO8起初叫Melo，为避免重名，改为PO8，谐音诗人poet，又有经典手枪鲁格PO8之意，所以PO8的作品里既有诗意，又有抗争。举枪的诗人要抗争什么？“在中国做说唱没有什么种族问题，不像美国，这边我们能看到的问题顶多是一些社会问题，这是需要有一批音乐人去发声的，还有就是现代人的意识形态。我希望通过我的歌曲，能给我的粉丝带去一些独立思考的能力或是说启发。”');
INSERT INTO `singer` VALUES (6, 'Eminem', 1, '/img/singerPic/1620305862846amu.jpg', '1972-10-17 00:00:00', '美国', '埃米纳姆（Eminem），原名马歇尔·布鲁斯·马瑟斯三世（Marshall Bruce Mathers III），1972年10月17日出生于美国密苏里州堪萨斯城，美国说唱歌手。');
INSERT INTO `singer` VALUES (7, 'GAI', 1, '/img/singerPic/1620306055743gai.jpg', '1987-03-22 00:00:00', '中国', 'GAI，中国内地嘻哈说唱男歌手。 2015年，推出个人嘻哈说唱单曲《白日梦想家》，从而正式进入演艺圈。2016年，以本名“周延”参加浙江卫视歌唱选秀节目《中国新歌声第一季》的比赛。');
INSERT INTO `singer` VALUES (8, 'C-BLOCK', 2, '/img/singerPic/1620306107376cblock.jpg', '2012-01-01 00:00:00', '中国', 'C-BLOCK来自湖南长沙 ，2007年组成，至10年组员正式确定为3名（大傻，key，小胖），一直以风趣幽默，贴近生活，美化世界的态度做着独立，有爱，有劲的hiphop音乐，早期以方言说唱成为本地年轻人最为热爱的团体，如今在12年加入sup music厂牌后，加上与来自香港hiphop制作人萧启道，乐团正在努力做出更多好听又具有特色的中文说唱，带来更多全新视听效果的现场。');
INSERT INTO `singer` VALUES (9, '王琳凯', 1, '/img/singerPic/1620306389477xiaogui.jpg', '1999-05-20 00:00:00', '中国', '鬼（Lil Ghost、AKA.imp），本名王琳凯，1999年5月20日出生于福建省，中国内地说唱男歌手、影视演员，就读于北京现代音乐研修学院。 2017年，参加爱奇艺Hip-Hop音乐选秀节目《中国有嘻哈》.');
INSERT INTO `singer` VALUES (10, 'TizzyT', 1, '/img/singerPic/1620306472584tt.jpg', '1993-02-22 00:00:00', '中国', 'TizzyT，优秀的年轻Hip-Hop音乐制作人和饶舌歌手。15岁开始接触Hip-Hop音乐，很快就展现出过人的Hip-Hop节奏触觉和天赋。早期的TT因对节奏的喜爱，进而刻苦练习BeatBox技巧，并在2010年就获得了华南地区“嘴上瘾”BeatBox大赛的冠军。');
INSERT INTO `singer` VALUES (11, 'Ice Cube', 1, '/img/singerPic/1620306548233icecube.jpg', '1969-06-19 00:00:00', '美国', '欧谢·杰克森（英语：O\'Shea Jackson, Sr.，1969年6月15日－），艺名为艾斯·库伯（Ice Cube），出生于美国加利福尼亚州洛杉矶，是美国著名饶舌艺人、演员、剧作家、电影导演和制片人。');
INSERT INTO `singer` VALUES (12, 'Higher Brothers', 2, '/img/singerPic/1620306686585highbro.jpg', '2016-07-01 00:00:00', '中国', 'Higher Brothers【美国CONTROL MUSIC GROUP 旗下艺人 / CDC说唱会馆旗下团体】 由MaSiWei / Dzknow / Psy.p / Melo四人于2016年组成的Higher Brothers，短短一年就成为中国说唱标杆的团体之一，现已签约美国公司Control Music Group（旗下艺人包括Keith Ape/Rich chigga/Father等）。');
INSERT INTO `singer` VALUES (13, 'VAVA', 0, '/img/singerPic/1620307335962vava.jpg', '1995-10-29 00:00:00', '中国', 'VaVa毛衍七，中国内地嘻哈说唱女歌手，YES MUSIC旗下艺人。');
INSERT INTO `singer` VALUES (14, '朴宰范', 1, '/img/singerPic/1620306826539piaozaifan.jpg', '1987-04-25 00:00:00', '韩国', '朴宰范，1987年4月25日出生于美国西雅图，流行歌手、舞者、音乐制作人，毕业于檀国大学。 2008年以偶像组合2pm队长身份出道，2009年退队，2010年初与JYP解除合约并签约韩国 SidusHQ公司。');

